// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract StakingWithEpoch is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        bool inBlackList;
        uint256 userEpoch;
        //
        // We do some fancy math here. Basically, any point in time, the amount of MAPIs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accRewardPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accRewardPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }
    // Info of each pool.
    struct PoolInfo {
        uint256 lastRewardBlock; // Last block number that SUSHIs distribution occurs.
        uint256 accRewardPerShare; // Accumulated SUSHIs per share, times 1e12. See below.

        uint256 totalAmount; // The total amount of current epoch
        uint256 startBlock;
        uint256 endBlock;
    }
    // The Reward TOKEN!
    IERC20 public rewardToken;

    //The lp token or soginal token
    IERC20 public lpToken;

    // adminAddress
    address public adminAddress;

    // MAPI tokens created per block.
    uint256 public rewardPerBlock;

    // Info of each user that stakes LP tokens.
    mapping(address => UserInfo) public userInfo;

    //current epoch
    uint256 internal currentEpoch;

    //Info of each epoch that use pool info
    mapping(uint256 => PoolInfo) public epochPool;

    event Deposit(address indexed user, uint256 indexed epoch, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed epoch, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        IERC20 _rewardToken,
        uint256 _rewardPerBlock,
        IERC20 _lpToken,
        uint256 _startBlock,
        uint256 _bonusEndBlock,
        address _adminAddress
    ) public {
        rewardToken = _rewardToken;

        rewardPerBlock = _rewardPerBlock;

        lpToken = _lpToken;

        adminAddress = _adminAddress;

        currentEpoch = 0;

        // staking pool
        epochPool[currentEpoch] = PoolInfo({
            lastRewardBlock: _startBlock,
            accRewardPerShare: 0,
            totalAmount: 0,
            startBlock: _startBlock,
            endBlock: _bonusEndBlock
        });
    }

    modifier onlyAdmin() {
        require(msg.sender == adminAddress, "admin: wrong sender");
        _;
    }

    function addEpochPool(uint256 _startBlock, uint256 _bonusEndBlock) public onlyOwner {
        currentEpoch = currentEpoch.add(1);

        // staking pool
        epochPool[currentEpoch] = PoolInfo({
            lastRewardBlock: _startBlock,
            accRewardPerShare: 0,
            totalAmount: 0,
            startBlock: _startBlock,
            endBlock: _bonusEndBlock
        });
    }

    //update pool info for specific epoch
    function updateEpochPool(uint256  _epoch, uint256 _startBlock, uint256 _bonusEndBlock ) public onlyOwner {
        require(_epoch <= currentEpoch, "PiStakingWithEpoch: wrong epoch");

        PoolInfo storage pool = epochPool[_epoch];
        pool.startBlock = _startBlock;
        pool.endBlock = _bonusEndBlock;
        pool.lastRewardBlock = _startBlock;
    }

    // Update admin address by the previous dev.
    function setAdmin(address _adminAddress) public onlyOwner {
        adminAddress = _adminAddress;
    }

    function setBlackList(address _blacklistAddress) public onlyAdmin {
        userInfo[_blacklistAddress].inBlackList = true;
    }

    function removeBlackList(address _blacklistAddress) public onlyAdmin {
        userInfo[_blacklistAddress].inBlackList = false;
    }

    function setRewardToken(IERC20 _reward) public onlyOwner {
        rewardToken = _reward;
    }

    function setRewardPerBlock(uint256 _rewardCount) public onlyOwner {
        rewardPerBlock = _rewardCount;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to,uint256 _epoch)
        public
        view
        returns (uint256)
    {
        PoolInfo storage pool = epochPool[_epoch];

        if (_to <= pool.endBlock) {
            return _to.sub(_from);
        } else if (_from >= pool.endBlock) {
            return _to.sub(_from);
        } else {
            return
                pool.endBlock.sub(_from).add(
                    _to.sub(pool.endBlock)
                );
        }
    }

    // View function to see pending Rewards on frontend.
    function pendingReward(address _user)
        external
        view
        returns (uint256)
    {
        UserInfo storage user = userInfo[_user];
        uint256 _epoch = user.userEpoch;
        require(_epoch <= currentEpoch, "PiStakingEpoch: wrong epoch");

        PoolInfo storage pool = epochPool[_epoch];

        uint256 accRewardPerShare = pool.accRewardPerShare;
        uint256 lpSupply = pool.totalAmount;

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number, _epoch);

            uint256 tokenReward = multiplier.mul(rewardPerBlock);

            accRewardPerShare = accRewardPerShare.add(tokenReward.mul(1e12).div(lpSupply)
            );
        }
        return user.amount.mul(accRewardPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _epoch) public {
        PoolInfo storage pool = epochPool[_epoch];

        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.totalAmount;
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number,_epoch);

        uint256 tokenReward = multiplier.mul(rewardPerBlock);

        pool.accRewardPerShare = pool.accRewardPerShare.add(tokenReward.mul(1e12).div(lpSupply));

        pool.lastRewardBlock = block.number;
    }

    function setMark() external {
        UserInfo storage user = userInfo[msg.sender];

        uint256 oldEpoch = user.userEpoch;
        PoolInfo storage oldPool = epochPool[oldEpoch];

        updatePool(oldEpoch);
        if (user.amount > 0) {
             uint256 pending = user.amount.mul(oldPool.accRewardPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                rewardToken.safeTransfer(address(msg.sender), pending);
            }
        }
        oldPool.totalAmount = oldPool.totalAmount.sub(user.amount);
        user.userEpoch = currentEpoch;
        user.rewardDebt = 0;

        PoolInfo storage currentPool = epochPool[currentEpoch];

        currentPool.totalAmount = currentPool.totalAmount.add(user.amount); 
        updatePool(currentEpoch);
    }


    // Deposit LP tokens to MasterChef for SUSHI allocation.
    function deposit(uint256 _amount) public {
        PoolInfo storage pool = epochPool[currentEpoch];

        UserInfo storage user = userInfo[msg.sender];

        if (user.amount == 0) {
            user.userEpoch = currentEpoch;
        }

        require(user.userEpoch == currentEpoch, "PiStakingWithEpoch: user epoch is wrong");

        require (!user.inBlackList, 'deposit: in black list');

        updatePool(currentEpoch);
        if (user.amount > 0) {
            uint256 pending =
                user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
                    user.rewardDebt
                );
            safeRewardTransfer(msg.sender, pending);
        }

        if (_amount > 0) {
            lpToken.safeTransferFrom(
                address(msg.sender),
                address(this),
                _amount
            );
            user.amount = user.amount.add(_amount);

            pool.totalAmount =  pool.totalAmount.add(_amount);
        }

        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
        emit Deposit(msg.sender, currentEpoch, _amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _amount) public {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        uint256 userEpoch = user.userEpoch;

        PoolInfo storage pool = epochPool[userEpoch];

        require(pool.totalAmount >= _amount, "withdraw: not good pool amount");

        require(user.amount >= _amount, "withdraw: not good");

        updatePool(userEpoch);
        uint256 pending =
            user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
                user.rewardDebt
            );

        if (pending > 0) {
            safeRewardTransfer(msg.sender, pending);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            lpToken.safeTransfer(address(msg.sender), _amount);
        }

        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);

        emit Withdraw(msg.sender, userEpoch, _amount);
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        UserInfo storage user = userInfo[msg.sender];
        lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // Safe rewardToken transfer function, just in case if rounding error causes pool to not have enough SUSHIs.
    function safeRewardTransfer(address _to, uint256 _amount) internal {
        uint256 rewardAmount = rewardToken.balanceOf(address(this));
        if (_amount > rewardAmount) {
            rewardToken.transfer(_to, rewardAmount);
        } else {
            rewardToken.transfer(_to, _amount);
        }
    }


    function getStaking(address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return user.amount;
    }

    function getUserEpoch(address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return user.userEpoch;
    }

    function getCurrentEpoch() public view returns (uint256) {
        return currentEpoch;
    }

    function getEpochBlockNumber(uint256 _epoch) public view returns (uint256, uint256) {
        PoolInfo storage pool = epochPool[_epoch];

        return (pool.startBlock, pool.endBlock);
    }

    function getTotalSupply() public view returns (uint256) {
        return lpToken.balanceOf(address(this));
    }

    function getPoolSupply(uint256 _epoch) public view returns (uint256) {
        PoolInfo storage pool = epochPool[_epoch];

        return pool.totalAmount;
    }
}
